import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import axios from 'axios'; // For making HTTP requests to the backend

const Home = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [username, setUsername] = useState(''); // State for username
    const navigate = useNavigate();
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [serviceProviders, setServiceProviders] = useState([]);
    const token = localStorage.getItem('token');

    // Check if JWT token exists in localStorage


    // Fetch service providers
    useEffect(() => {
        fetch('http://localhost:5009/api/service-providers')
            .then((response) => response.json())
            .then((data) => {
                console.log('Received Service Providers:', data);
                if (data && data.length > 0) {
                    setServiceProviders(data);
                } else {
                    setServiceProviders([]);
                }
            })
            .catch((error) => {
                console.error('Error fetching service providers:', error);
                setServiceProviders([]);
            });
    }, []);

    const handleSearch = async () => {
        try {
            const response = await fetch(`http://localhost:5009/search?query=${query}`);
            const results = await response.json();
            setSearchResults(results);
        } catch (error) {
            console.error('Error fetching search results:', error);
        }
    };

    const handleProviderClick = (providerId) => {
        navigate(`/provider-profile/${providerId}`);
    };

    const handleSignupClick = () => {
        if (!isLoggedIn) {
            // Redirect to register page if not logged in
            navigate('/register');
        } else {
            // Show "Hello, username" if already logged in
            alert(`Hello, ${username}`);
        }
    };

    return (
        <div className="home-container">
            <header className="home-header">
                <h1>TheMoveAura</h1>
                <button onClick={handleSignupClick} className="profile-button">
                    {isLoggedIn ? `Hello, ${username}` : 'Sign Up'}
                </button>
            </header>

            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search for sports, customers, or service providers"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button onClick={handleSearch}>Search</button>
            </div>

            <div className="search-results">
                {searchResults.map((item, index) => (
                    <div key={index} className="result-item">
                        <h3>{item.name}</h3>
                        {item.type && <p>Type: {item.type}</p>}
                    </div>
                ))}
            </div>

            <div className="map-container">
                <iframe
                    title="Map"
                    src="https://www.google.com/maps/embed?pb=..."
                    width="600"
                    height="400"
                    style={{ border: 0 }}
                    allowFullScreen=""
                    loading="lazy"
                ></iframe>
            </div>

            <div className="providers-gallery">
                {serviceProviders.map((provider) => (
                    <div
                        key={provider.id}
                        className="provider-item"
                        onClick={() => handleProviderClick(provider.id)}
                    >
                        <img src={provider.imageUrl} alt={provider.name} className="provider-image" />
                        <h3>{provider.name}</h3>
                        <p>{provider.description}</p>
                        <span>Rating: {provider.rating}</span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
